import React, { useEffect, useState } from "react";
import KeplerGl from "kepler.gl";
import { addDataToMap } from "kepler.gl/actions";
import { useDispatch } from "react-redux";
import helpers from "./helpers";


const DATA_URL =
  "https://github.com/tugcece1/keplergl-react-app-api-data/blob/master/data2.json";

const sampleConfig = {
  visState: {
    filters: [
      {
        id: "dateFilter_id",
        dataId: "date_id",
        name: "the name of the column to be filtered",
        type: "timeRange",//filter type to be use
      },
    ],

  },
};



function Map() {
  const dispatch = useDispatch();
  const [data, setData] = useState();

  const fetchData = async () => {
    setData(await helpers.httpGet(DATA_URL));
  };
  useEffect(() => {
    fetchData();
  }, []);

  useEffect(() => {
    data &&
      dispatch(
        addDataToMap({
          datasets: {
            info: {
              label: "RECENT EARTHQUAKES IN TURKEY AND ITS ENVIRONMENT",
              id: "EARTHQUAKES",
            },
            data: data,
          },
          option: {
            centerMap: true,
            readOnly: false,
          },
          config: sampleConfig,
        })
      );
  }, [dispatch, data]);

  <KeplerGl 
  id="map" 
  width={window.innerWidth} 
  height={window.innerHeight} 
  mapboxApiAccessToken="pk.eyJ1IjoidHVnY2UtdGF5IiwiYSI6ImNsZGJwcDJ2NzAydTgzb2xqd3V6dTJkNTgifQ.i_tA-z-uZT3FIU1-308Scw" 
/>


}
export default Map;
